package practicas.GomezGarciaJoseAlberto_p1.controller;

import java.io.IOException;
import java.io.InputStream;
import java.util.Base64;
import java.util.List;

import com.fasterxml.jackson.core.JsonProcessingException;
import com.fasterxml.jackson.databind.ObjectMapper;

import jakarta.ws.rs.*;
import jakarta.ws.rs.core.MediaType;
import jakarta.ws.rs.core.Response;
import jakarta.ws.rs.core.Response.Status;
import practicas.GomezGarciaJoseAlberto_p1.cookies.SessionManager;
import practicas.GomezGarciaJoseAlberto_p1.modelo.Producto;
import practicas.GomezGarciaJoseAlberto_p1.services.ProductoServiceImpl;

@Path("productos")
public class ProductoController {
	
	private String BASE_URI = "http://localhost:8080/GomezGarciaJoseAlberto-p1/rest/productos/";
	private ProductoServiceImpl productoService = new ProductoServiceImpl();

	@GET
	@Produces(MediaType.APPLICATION_JSON)
    public Response findAll() throws JsonProcessingException {
		List<Producto> listaProductos = productoService.getAll();
		String json =  new ObjectMapper().writeValueAsString(listaProductos);
        return Response.ok(json).build();
    }

	@GET
	@Path("{id}")
	@Produces(MediaType.APPLICATION_JSON)
	public Response getById(@PathParam("id") Long id) throws Exception {
		try {
			Producto producto = productoService.get(id);
			String json =  new ObjectMapper().writeValueAsString(producto);
			return Response.ok(json).build();
		} catch (Exception e) {
			return Response
					.status(Status.NOT_FOUND)
					.entity("{'error': 'Producto con id '" + id + "' no encontrado'.}")
					.build();
		}
	}

	@POST
	@Consumes(MediaType.APPLICATION_JSON)
	@Produces(MediaType.APPLICATION_JSON)
	public Response addProducto(@CookieParam("sessionId") String sessionId,
									Producto producto) throws IOException {
		if (sessionId == null) {
			return Response
					.status(Status.UNAUTHORIZED)
					.entity("{'error': No se ha iniciado sesión.}")
					.build();
		}
		if (!SessionManager.getInstance().getUsuario(sessionId).isAdmin()) {
			return Response
					.status(Status.UNAUTHORIZED)
					.entity("{'error': No tienes permisos para realizar esta acción. Debes ser administrador.}")
					.build();
		}

		
		if (producto.getImagen() == null) 
			producto.setImagen("");
			// Sin imagen enviada (salta check de HTML), y saltando check de JavaScript, no debería suceder.
		
		try {
			productoService.add(producto);
			return Response
					.status(Status.CREATED)
					.header("Location", BASE_URI + producto.getId())
					.entity("{result: Producto creado con éxito}")
					.build();
		} catch (Exception e) {
			return Response
					.status(Status.CONFLICT)
					.entity("{error: " + e.getMessage() + "}")
					.build();
		}
	}


	@PUT
	@Path("{id}")
	@Consumes(MediaType.APPLICATION_JSON)
	@Produces(MediaType.APPLICATION_JSON)
	public Response updateProducto(@PathParam("id") Long id, 
								   @CookieParam("sessionId") String sessionId,
								   Producto product) {
		if (sessionId == null) {
			return Response
					.status(Status.UNAUTHORIZED)
					.entity("{'error': No se ha iniciado sesión.}")
					.build();
		}
		if (!SessionManager.getInstance().getUsuario(sessionId).isAdmin()) {
			return Response
					.status(Status.UNAUTHORIZED)
					.entity("{'error': No tienes permisos para realizar esta acción. Debes ser administrador.}")
					.build();
		}
		try {
			productoService.edit(id, product);
			return Response
					.status(Status.OK)
					.entity("{result: Producto modificado con éxito}")
					.build();
		} catch (Exception e) {
			return Response
					.status(Status.INTERNAL_SERVER_ERROR)
					.entity("{error: " + e.getMessage() + "}")
					.build();
		}
	}

	@DELETE
	@Path("{id}")
	@Produces(MediaType.APPLICATION_JSON)
	public Response deleteProducto(@CookieParam("sessionId") String sessionId,
								   @PathParam("id") Long id) {
		if (sessionId == null) {
			return Response
					.status(Status.UNAUTHORIZED)
					.entity("{'error': No se ha iniciado sesión.}")
					.build();
		}
		if (!SessionManager.getInstance().getUsuario(sessionId).isAdmin()) {
			return Response
					.status(Status.UNAUTHORIZED)
					.entity("{'error': No tienes permisos para realizar esta acción. Debes ser administrador.}")
					.build();
		}
		try {
			productoService.delete(id);
			return Response
					.status(Status.OK)
					.entity("{result: Producto eliminado con éxito}")
					.build();
		} catch (Exception e) {
			return Response
					.status(Status.NOT_FOUND)
					.entity("{error: " + e.getMessage() + "}")
					.build();
		}
	}

}
